import React, { useEffect, useState } from 'react'
import {API_KEY,IMAGE_URL} from '../../Constants/Constants'
import './Banner.css'
import axios from '../../axios'
import YouTube from 'react-youtube';
import {useNavigate} from 'react-router-dom'

function Banner() {


  const rndInt = Math.floor(Math.random() * 20) + 1

 const [movie,setMovie] = useState({})
 const [banner,setBanner] = useState('')

  useEffect(()=>{
    axios.get(`trending/all/week?api_key=${API_KEY}&language=en-US`)
    .then((response)=>{
      setMovie(response.data.results[rndInt])
    })
  },[])

  const bannerPlay = (id)=>{

    // axios.get(`/movie/${id}/videos?api_key=${API_KEY}&language=en-US`)
    // .then((response)=>{
    //   setBanner(response.data.results[0])
    // })
  }

  const opts = {
    height: '390',
    width: '100%',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };

  return (
    <div style={{backgroundImage:`url(${movie ? IMAGE_URL+movie.backdrop_path : ''})`}} className='banner'>
        <div className='content'>
            <h1 className='title'>
              {movie ? movie.title : 'Movie Name'}
            </h1>
            <div className='banner-buttons'>
                <button onClick={()=>{bannerPlay(movie.id)}} className='button'>play</button>
                <button className='button'>Mylist</button>
            </div>
            <h1 className='discription'>{movie ? movie.overview : movie.name}</h1>
            <div className="fade-bottom"></div>
        </div>
        {banner &&  <YouTube videoId={banner.key} opts={opts}  />}

    </div>
  )
}

export default Banner
